import React, { Component } from 'react';
class CounterItem extends Component {
    state = {
        count: 0
    };
    addCount = () => {
        this.setState(({ count }) => ({
            count: count + 1
        }));
    };
    render() {
        return <button onClick={this.addCount}>{this.state.count}</button>;
    }
}
export default CounterItem
