import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import Login from './components/login/login';
import Details from './components/counter-details/counter-details';
import * as serviceWorker from './serviceWorker';
import * as Redux from 'redux';
import * as ReactRedux from 'react-redux';
import { BrowserRouter, Route } from 'react-router-dom';



const initialState = {
    countersLimit: 0,
    listCounters: [],
    count: 0,
    userLogin: ''
};

function reducer(state = initialState, action) {
    switch (action.type) {
        case 'ADD_COUNTER':
            return Object.assign(
                {},
                state, {
                    listCounters: state.listCounters.concat([action.newCounter]),
                    count: state.count + 1
                });
        case 'DELETE_COUNTER':
            let newCounters = [
                ...state.listCounters
            ];
            newCounters.splice(action.indexCounter, 1);
            return {
                ...state,
                listCounters: newCounters,
                count: state.count - 1
            };
        case 'ADD_LIMIT':

            return Object.assign(
                {},
                state, {
                    countersLimit: action.countersLimit
                }
            );
        case 'ADD_USER':
            return Object.assign(
                {},
                state, {
                    userLogin: action.userLogin
                }
            )
        case 'DELETE_USER':
            return Object.assign(
                {},
                state, {
                    userLogin: ''
                }
        )
        case 'INCREASE_COUNTER':
        console.log(action.counter);
            return Object.assign(
                {},
                state.listCounters, {
                    [action.counter]: {
                        id: state.listCounters[action.counter].id,
                        name: state[action.counter].name,
                        clickCount: state[action.counter].clickCount + 1,
                        blocked: state[action.counter].blocked,
                        isFirst: state[action.counter].isFirst,
                        firstClick: state[action.counter].firstClick,
                        lastClick: state[action.counter].lastClick
                    }
                }
        )



        default:
            return state;
    }
}

let store = Redux.createStore(
    reducer,
    window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__());


ReactDOM.render(
    <BrowserRouter>
        <ReactRedux.Provider store={store}>
            <React.Fragment>
                <Route exact path="/" component={Login} />
                <Route exact path="/app" component={App} />

            </React.Fragment>
        </ReactRedux.Provider>
    </BrowserRouter>,
    document.getElementById('root')
);
serviceWorker.unregister();
