import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom'
import './header.css'



class Header extends Component {

  handleSelected() {
    console.log(this.props.countersLimit)
    if (this.props.countersLimit > 0 && this.props.listCounters.length < this.props.countersLimit) {
      this.props.onAddCounter();
    }
  }

    render() {

        return (
          <header>
            <nav>
              <ul>
                <li>Welcome {this.props.userLogin}</li>
                <li><button onClick={() => { this.handleSelected() }}>Logout</button></li>
              </ul>
            </nav>
          </header>
        )
    }
}
const mapStateToProps = (state) => {
    return {
        userLogin: state.userLogin,
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onAddUser: (userLogin) => {
            dispatch({ type: 'ADD_USER', userLogin });
        }
    };
}

Header.propTypes = {
    userLogin: PropTypes.string,
    history: PropTypes.object
}

export default connect(mapStateToProps, mapDispatchToProps)(Header);
