import React, { Component } from 'react';
import CounterItem from './components/counter-item/counter-item'
import './App.css';

class App extends Component {
  state = {
    listCounters: ["1", "2", "3"]
  };

  addCounter = () => {
    this.setState({
      listCounters: [...this.state.listCounters, "4"]
    });
  };

  render() {
    return (
      <div className="App">
        <button onClick={() => { this.addCounter() }}>Agregar contador</button>
        {this.state.listCounters.map(idCounter => <CounterItem key={idCounter} id={idCounter} />)}
      </div>
    );
  }
}

export default App;
