import React, { Component } from 'react';
import CounterItem from './components/counter-item/counter-item'
import './App.css';

class App extends Component {
  state = {
    listCounters: ["1", "2", "3"],
    count: 3
  };

  addCounter = () => {
    this.setState({
      listCounters: [...this.state.listCounters, "4"]
    });
    this.setState(({ count }) => ({
      count: count + 1
    }));
  };

  render() {
    return (
      <div className="App">
        <header>
          <h2>Contador {this.state.count}</h2>
          <button onClick={() => { this.addCounter() }}>Agregar contador</button>
        </header>

        {this.state.listCounters.map(idCounter => <CounterItem key={idCounter} id={idCounter} />)}
      </div>
    );
  }
}

export default App;
