import React, { Component } from 'react';
import CounterItem from '../counter-item/counter-item'
import { connect } from 'react-redux';

class CounterContainer extends Component {

    constructor(props) {
        super(props);

        this.handleSelected = this.handleSelected.bind(this);
    }
    state = {
        clickCount: 0
    };
    increase = () => {
        this.setState(({ count }) => ({
            count: count + 1
        }));
    };
    reset = () => {
        this.setState(({ count }) => ({
            count: 0
        }));
    }

    handleSelected(indexCounter) {
        this.props.onDeleteCounter(indexCounter);
    }
    render() {
        console.log(this.props.id);
        console.log(this.props.ind);
        return (
            <div className="counter-container">
                <button onClick={this.increase}>{this.state.count}</button>
                <button className="Delete-button" onClick={() => { this.handleSelected(this.props.ind) }}>Delete counter</button>
                <button className="Reset-button" onClick={this.reset}>Reset counter</button>
                <button className="Block-button" >Block counter</button>
            </div>
        )
    }
}
const mapStateToProps = (state) => {
    return {
        count: state.count,
        listCounters: state.listCounters
    };
}

const mapDispatchToProps = (dispatch) => {
    return {
        onDeleteCounter: (indexCounter) => {
            dispatch({ type: 'DELETE_COUNTER', indexCounter });
        }
    };
}
export default connect(mapStateToProps, mapDispatchToProps)(CounterContainer);
